package bitcamp.lms;
import java.util.Scanner;
import java.sql.Date;

public class App2 {

  public static void main(String[] args) {
    
    int[] num = new int[100];
    String[] name = new String[100];
    String[] email = new String[100];
    String[] phone = new String[100];
    Date[] today = new Date[100];
    
    Scanner sc = new Scanner(System.in);
    
    int i = 0;

    while(true) {

      System.out.print("번호? ");
      num[i] = Integer.parseInt(sc.nextLine());

      System.out.print("이름? ");
      name[i] = sc.nextLine();

      System.out.print("이메일? ");
      email[i] = sc.nextLine();

      System.out.print("암호? ");
      String pwd = sc.nextLine();

      System.out.print("사진? ");
      String photo = sc.nextLine();

      System.out.print("전화? ");
      phone[i] = sc.nextLine();

      System.out.print("계속 하시겠습니까?(Y/n)");
      String str = sc.nextLine();
      
      today[i] = new Date(System.currentTimeMillis());
      
      i++;
      
      if(str.equalsIgnoreCase("y") ||str.equalsIgnoreCase("")) {
        continue;
      }
      if (!str.equalsIgnoreCase("y") || !str.equalsIgnoreCase("")) {
        int j;
        for(j = 0 ; j < i ; j++ ) {
          System.out.printf("%d, %s, %s, %s, %s\n", num[j], name[j], email[j], phone[j], today[j]);
        } break;
      }

    }
  }
}
